xqdoc:function.xqdoc:body:
let $basex:=$pkg/pkg:package/pkg:dependency[@processor="http://basex.org/"]/@version/string() let $pkgs:=$pkg/pkg:package/pkg:dependency[@name] return
if( db:system()/generalinformation/version/tokenize(.," ")[1] ne $basex)then error(xs:QName("xpg:version"),"BaseX version not supported") else ( for $p in $pkgs return if(repo:list()[@name=$p/@name]/@version ne $p/@version) then error(xs:QName("pkg:version"),$p/@name) else ()
,"xqDocA dependancies are all installed." )

let $basex:=$pkg/pkg:package/pkg:dependency[@processor="http://basex.org/"]/@version/string() let $pkgs:=$pkg/pkg:package/pkg:dependency[@name] return
if( db:system()/generalinformation/version/tokenize(.," ")[1] ne $basex)then error(xs:QName("xpg:version"),"BaseX version not supported") else ( for $p in $pkgs return if(repo:list()[@name=$p/@name]/@version ne $p/@version) then error(xs:QName("pkg:version"),$p/@name) else ()
,"xqDocA dependancies are all installed." )
