xqdoc:function.xqdoc:body:
function _:xqdoc($file as map(*), 
                 $model as map(*),
                 $opts as map(*)
                 )
{
  $file?xqdoc
}

function _:xqparse($file as map(*),
                   $model as map(*),
                   $opts as map(*)
                   )
{
  $file?xqparse
}
